import { useState } from "react";
import PortfolioList from "../portfolioList/PortfolioList"
import "./portfolio.css"
import {
  featuredPortfolio,
  webPortfolio,
  mobilePortfolio,
  designPortfolio,
  contentPortfolio,
} from "../../data.js"
import { useEffect } from "react";

export default function Portfolio() {
  const [selected, setSelected] = useState("featured")
  const [data, setData] = useState([])

  useEffect(() => {

    switch (selected) {
      case "Featured":
        setData(featuredPortfolio);
        break;
      case "Featured":
        setData(webPortfolio);
        break;
      case "web":
        setData(mobilePortfolio);
        break;
      case "mobile":
        setData(designPortfolio);
        break;
      case "design":
        setData(contentPortfolio);
        break;
      case "content":
        setData(contentPortfolio);
        break;
      default:
        setData(featuredPortfolio);
    }
  }, [selected])
  const list = [
    {
      id: "featured",
      title: "Featured",
    },
    {
      id: "web",
      title: "Web App",
    },
    {
      id: "mobile",
      title: "Mobile App",
    },
    {
      id: "design",
      title: "Design",
    },
    {
      id: "content",
      title: "Content",
    },
  ];

  return (
    <div className="portfolio">
      <h1 className="port">Portfolio</h1>
      <ul className="list-ul">
        {list.map(item => (
          <PortfolioList
            title={item.title}
            active={selected === item.id}
            setSelected={setSelected}
            id={item.id}
          />
        ))}
      </ul>
      <div className="container">
        <div className="item">
          <img src="https://th.bing.com/th/id/OIP.myVUFVG95_SWjAX5IsjFVwHaLJ?pid=ImgDet&rs=1" alt="" />
          <h2>Banking App</h2>
        </div>
        <div className="item">
          <img src="https://th.bing.com/th/id/OIP.OsaQYOYKVnvVdQMM4bjwnQHaLH?pid=ImgDet&rs=1" alt="" />
          <h2>Banking App</h2>
        </div>
        <div className="item">
          <img src="https://th.bing.com/th/id/OIP.UM1LSTx8ukwqIpPOjcPGBAHaJi?pid=ImgDet&w=497&h=640&rs=1" alt="" />
          <h2>Banking App</h2>
        </div>
        <div className="item">
          <img src="https://th.bing.com/th/id/OIP.UM1LSTx8ukwqIpPOjcPGBAHaJi?pid=ImgDet&w=497&h=640&rs=1" alt="" />
          <h2>Banking App</h2>
        </div>
        <div className="item">
          <img src="https://th.bing.com/th/id/OIP.UM1LSTx8ukwqIpPOjcPGBAHaJi?pid=ImgDet&w=497&h=640&rs=1" alt="" />
          <h2>Banking App</h2>
        </div>
        <div className="item">
          <img src="https://th.bing.com/th/id/OIP.UM1LSTx8ukwqIpPOjcPGBAHaJi?pid=ImgDet&w=497&h=640&rs=1" alt="" />
          <h2>Banking App</h2>
        </div>
      </div>
    </div>
  )
}
