import { useState } from "react";
import "./slider.css"

export default function Slider() {

  const [currentSlide, setCurrentSlide] = useState(0)

  const data = [
    {
      id: "1",
      icon: "./assets/mobile.png",
      title: "Web Design",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry. ",
      img:
        "https://99designs-blog.imgix.net/blog/wp-content/uploads/2018/10/attachment_100040756-e1538485934255.jpeg?auto=format&q=60&fit=max&w=930",
    },
    {
      id: "2",
      icon: "./assets/globe.png",
      title: "Mobile Application",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
      img:
        "https://i.pinimg.com/originals/e9/c9/2f/e9c92f7869d682a6fa5a97fb8a298f30.jpg",
    },
    {
      id: "3",
      icon: "./assets/writing.png",
      title: "Branding",
      desc:
        "Lorem Ipsum is simply dummy text of the printing and typesetting industry.",
      img:
        "https://i.pinimg.com/originals/a9/f6/94/a9f69465d972a004ad581f245d6ad581.jpg",
    },
  ];

  const handleClick = (way) => {
    way === "left"
      ? setCurrentSlide(currentSlide > 0 ? currentSlide - 1 : 2)
      : setCurrentSlide(currentSlide < data.length - 1 ? currentSlide + 1 : 0);
  }

  return (
    <div className="slider">
      <div className="slider-content"
        style={{ transform: `translateX (-${currentSlide * 100}vw)` }}>

        {data.map(d => (
          <div className="slider-item">
            <div className="left">
              <div className="left-con">
                <div className="img-cont">
                  <i class="fa-solid fa-mobile"></i>
                </div>
                <h2 className="title">{d.title}</h2>
                <div className="desc">{d.desc}</div>
                <span >Projects</span>
              </div>
            </div>
            <div className="right">
              <img className="slider-img" src="https://99designs-blog.imgix.net/blog/wp-content/uploads/2018/10/attachment_100040756-e1538485934255.jpeg?auto=format&q=60&fit=max&w=930" alt="" />
            </div>
          </div>
        ))}
      </div>
      <i class="arrow left fa-solid fa-arrow-left"
        onClick={() => handleClick("left")}
      ></i>
      <i class="arrow right fa-solid fa-arrow-right"
        onClick={() => handleClick()}
      ></i>
    </div>
  )
}
