import { useState } from "react"
import "./contact.css"

export default function Contact() {
  const [message, setMessage] = useState(false)

  function handleSubmit(e) {
    e.preventDefault();
    setMessage(true)
  }
  return (
    <div className='contact'>
      <div className="contact-img">
        <img src="https://99designs-blog.imgix.net/blog/wp-content/uploads/2018/10/attachment_100040756-e1538485934255.jpeg?auto=format&q=60&fit=max&w=930" alt="" />
      </div>
      <div className="contact-right">
        <h1>Contact.</h1>
        <form onSubmit={handleSubmit}>
          <input type="text" placeholder="E-mail" />
          <textarea name="" id="" cols="30" rows="10" placeholder="Message"></textarea>
          <button type="submit" className="sub-btn">Send</button>
          {message && <span className="msg">thanks, I'll reply ASAP:)</span>}
        </form>
      </div>
    </div>
  )
}
